import Joi from 'joi';
import { CurrencySchema } from './currency-schema';

export const AdvancedOptionsSchema = Joi.object({
  contains_alcohol: Joi.boolean().optional(),
  no_postage: Joi.boolean().optional(),
  nonmachineable: Joi.boolean().optional(),
  bill_duties_to_sender: Joi.boolean().optional(),
  saturday_delivery: Joi.boolean().optional(),
  tip: CurrencySchema.optional(),
}).pattern(/.*/, Joi.any());
