import Joi from 'joi';
import { TaxIdentifierSchema } from '../taxes/tax-identifier-schema';

export const ImporterOfRecordSchema = Joi.object({
  tax_identifiers: Joi.array().items(TaxIdentifierSchema).optional(),
  name: Joi.string().optional(),
  company_name: Joi.string().optional(),
  address_line1: Joi.string().required(),
  address_line2: Joi.string().optional(),
  city_locality: Joi.string().optional(),
  state_province: Joi.string().optional(),
  postal_code: Joi.string().optional(),
  country_code: Joi.string().optional(),
  phone: Joi.string().optional(),
  email: Joi.string().optional(),
});
