import Joi from 'joi';
import { RelayPointAddressSchema } from './relay-point-address-schema';
import { OpeningTimesSchema } from './opening-times-schema';

export const RelayPointSchema = RelayPointAddressSchema.keys({
  phone: Joi.string().optional(),
  longitude: Joi.number().optional(),
  latitude: Joi.number().optional(),
  opening_times: OpeningTimesSchema.required(),
});
