import Joi from 'joi';
import { DimensionsSchema } from './dimensions-schema';
import { DimensionUnitSchema } from './dimension-unit-schema';

export const DimensionDetailsSchema = Joi.object({
  dimensions_in_centimeters: DimensionsSchema.optional(),
  dimensions_in_inches: DimensionsSchema.optional(),
  source_dimensions: DimensionsSchema.optional(),
  source_dimension_unit: DimensionUnitSchema.required(),
});
