import Joi from 'joi';
import { WeightUnitSchema } from './weight-unit-schema';

export const WeightDetailsSchema = Joi.object({
  weight_in_ounces: Joi.number().optional,
  weight_in_grams: Joi.number().optional,
  source_weight: Joi.number().optional,
  source_weight_unit: WeightUnitSchema.required(),
});
