import Joi from 'joi';
import { TrackingInfoSchema } from '../models/tracking/tracking-info-schema';
import { UpdateMethodsSchema } from '../models/tracking/update-method-schema';
import { BaseResponseSchema } from './base-response-schema';

export const NormalizeTrackingDataResponseSchema = BaseResponseSchema.keys({
  tracking_info: Joi.array().required().items(TrackingInfoSchema),
  update_method: UpdateMethodsSchema,
});
