import { HandleError, LogError, LogInfo } from '../../src';
import { logger } from '@shipengine/connect-runtime';

describe('Logic helper', function () {
  test('HandleErrorShouldThrowCoreExcetption', () => {
    expect(() => HandleError('Test')).toThrowError(Error);
  });

  test('Is logger called for LogInfo method. Expected is called.', () => {
    const spyOn = jest.spyOn(logger, 'info');

    LogInfo('Test');

    expect(spyOn).toHaveBeenCalled();
  });

  test('Is logger called for LogError method. Expected is called.', () => {
    const spyOn = jest.spyOn(logger, 'error');

    LogError('Test2');

    expect(spyOn).toHaveBeenCalled();
  });
});
