import { FakeCustomItemBuilder } from '../../src';

describe('Create Fake custom item object tests', function () {
  test('Set country of origin. Expected Poland', () => {
    const customItem = new FakeCustomItemBuilder()
      .SetCountryOfOrigin('Poland')
      .Build();

    expect(customItem.country_of_origin).toBe('Poland');
  });

  test('Set Quantity. Expected 5.', () => {
    const customItem = new FakeCustomItemBuilder()
      .SetQuantity(5)
      .Build();

    expect(customItem.quantity).toBe(5);
  });

  test('Set Description. Expected my description.', () => {
    const customItem = new FakeCustomItemBuilder()
      .SetDescription('my description')
      .Build();

    expect(customItem.description).toBe('my description');
  });

  test('Set Sku. Expected 123456789', () => {
    const customItem = new FakeCustomItemBuilder().SetSku('123456789')
      .Build();

    expect(customItem.sku).toBe('123456789');
  });

  test('Set Sku description. Expected DISC', () => {
    const customItem = new FakeCustomItemBuilder().SetSkuDescription('DISC')
      .Build();

    expect(customItem.sku_description).toBe('DISC');
  });
});
