import { FakeGetTrackRequestBuilder } from '../../src';

describe('Create Fake get track request', function () {
  test('Set identifier. Expected tackingnumber 123456789.', () => {
    const request = new FakeGetTrackRequestBuilder()
      .SetIdentifiers([{ type: 'trackingnumber', value: '123456789' }])
      .Build();

    expect(request.identifiers).toStrictEqual([{type: 'trackingnumber', value: '123456789'}]);
  });

  test('Set identifiers. Expected trackingnumber 123456789, testnumber 000000000.', () => {
    const request = new FakeGetTrackRequestBuilder()
        .SetIdentifiers([{ type: 'trackingnumber', value: '123456789' }, {type: 'testnumber', value: '000000000'}])
        .Build();

    expect(request.identifiers).toStrictEqual([{type: 'trackingnumber', value: '123456789'}, {type: 'testnumber', value: '000000000'}]);
  });

  test('Set metadata. Expected {"key": "value"}.', function () {
    const request = new FakeGetTrackRequestBuilder()
      .SetMetadata({ key: 'value' })
      .Build();

    expect(request.metadata).toEqual({ key: 'value' });
  });
});
