import { FakeRegisterRequestBuilder } from '../../src';

describe('Create fake register request tests', function () {
  test('Set Transaction Id. Expected 7f8ea309-45b3-4038-ad0a-583d0b17ce64', () => {
    const request = new FakeRegisterRequestBuilder()
      .SetTransactionId('7f8ea309-45b3-4038-ad0a-583d0b17ce64')
      .Build();

    expect(request.transaction_id).toBe('7f8ea309-45b3-4038-ad0a-583d0b17ce64');
  });

  test('Set Register Info. Expected same object.', () => {
    const registerInfo = {
      key: 'value'
    };

    const request = new FakeRegisterRequestBuilder()
      .SetRegisterInfo(registerInfo)
      .Build();

    expect(request.registration_info).toBe(registerInfo);
  });
});
