import {AxiosRequestConfig, AxiosResponse, AxiosResponseHeaders} from 'axios';

export class FakeAxiosResponse implements AxiosResponse {
    static Build(config: AxiosRequestConfig<any> = {},
                 data: any = {}, headers: AxiosResponseHeaders = {},
                 status: number = 500,
                 statusText: string = ''): FakeAxiosResponse {

        return new FakeAxiosResponse(config, data, headers, Number(status), statusText);
    }

    constructor(config: AxiosRequestConfig<any>, data: any, headers: AxiosResponseHeaders, status: number, statusText: string) {
        this.config = config;
        this.data = data;
        this.headers = headers;
        this.status = status;
        this.statusText = statusText;
    }

    config: AxiosRequestConfig<any>;
    data: any;
    headers: AxiosResponseHeaders;
    status: number;
    statusText: string;
}