import {IRefreshAuthProcess} from '../../src/third-party-connector/connector/i-refresh-auth-process';
import {ThirdPartyRequest} from '../../src/third-party-connector/request/third-party-request';
import {ThirdPartyConnector} from '../../src/third-party-connector/connector/third-party-connector';

// generic class extended by module one to include the response and error models
export class ModuleThirdPartyConnector extends ThirdPartyConnector {

  constructor() {
    super('ModuleThirdPartyConnector');
  }

}

export class RefreshDEMO implements IRefreshAuthProcess {
  constructor(private readonly carrierData: { [key: string]: any }) {
  }

  IsRefreshRequired(): boolean {
    //based on Metadata
    return false;
  }

  RefreshBehaviour(): ThirdPartyRequest {
    return new ThirdPartyRequest();
  }

  UpdateMetadata(metadata:any): any {
  }

  ApplyRefreshBehaviour(response: any, base: ThirdPartyRequest): void {
    base.Headers([{'Token': `${response?.token}`}]);
  }

}