import { ThirdPartySoapRequest } from "../../../src/third-party-soap-connector/request/third-party-soap-request";

describe('Soap request tests', () => {
    let soapRequest: ThirdPartySoapRequest<unknown>;

    beforeEach(() => {
        soapRequest = new ThirdPartySoapRequest<unknown>();
    });

    test('Data should set the data property', () => {
        // Arrange
        const data = {
            'key': 'value'
        };

        // Act
        soapRequest.Data(data);

        // Assert
        expect(soapRequest.data).toEqual(data);
    });

    test('Method should set the method property', () => {
        // Arrange
        const method = 'method';

        // Act
        soapRequest.Method(method);

        // Assert
        expect(soapRequest.method).toEqual(method);
    });
});