import { ThirdPartySoapResponse } from "../../../src/third-party-soap-connector/request/third-party-soap-response";

describe('Soap response tests', () => {
    let soapResponse: ThirdPartySoapResponse<unknown>;

    test('Constructor should set the data property', () => {
        // Arrange
        const data = {
            'key': 'value'
        };

        // Act
        soapResponse = new ThirdPartySoapResponse<unknown>(data, {});

        // Assert
        expect(soapResponse.data).toEqual(data);
    });
    
    test('Constructor should set the headers property', () => {
        // Arrange
        const headers = {
            'header1': 'value1',
            'header2': 'value2'
        };

        // Act
        soapResponse = new ThirdPartySoapResponse<unknown>({}, headers);

        // Assert
        expect(soapResponse.headers).toEqual(headers);
    });
});